mapper.select:
select service, title, description as content, url
from search
where service = #{Cate}
		and (

			title like CONCAT('%',#{item},'%') or contents like CONCAT('%',#{item},'%')

		)

select * 
from search_keyword 
where pair_code in (
			select pair_code from search_keyword where keyword_txt = #{search_txt}
		)
